import React, { useState, useEffect } from 'react';
import './App.css';
import { Plus, Edit2, Trash2, Check, X, User, LogOut, Search, Filter } from 'lucide-react';

const getStoredTasks = () => {
  const tasks = localStorage.getItem('tasks');
  return tasks ? JSON.parse(tasks) : [];
};

const getStoredUser = () => {
  return localStorage.getItem('currentUser');
};

const storeTasks = (tasks) => {
  localStorage.setItem('tasks', JSON.stringify(tasks));
};

const storeUser = (username) => {
  localStorage.setItem('currentUser', username);
};

const clearUser = () => {
  localStorage.removeItem('currentUser');
};

const Login = ({ onLogin }) => {
  const [username, setUsername] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    if (username.trim()) {
      storeUser(username);
      onLogin(username);
    }
  };

  return (
    <div className="container login-container">
      <form onSubmit={handleSubmit} className="login-form">
        <div className="login-header">
          <div className="login-icon"><User size={32} /></div>
          <h1>Task Tracker</h1>
          <p>Enter your username to continue</p>
        </div>
        <input
          type="text"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
          placeholder="Enter your username"
          required
        />
        <button type="submit">Login</button>
      </form>
    </div>
  );
};

const TaskForm = ({ onAddTask, onCancel, editingTask, onUpdateTask }) => {
  const [title, setTitle] = useState(editingTask?.title || '');
  const [description, setDescription] = useState(editingTask?.description || '');

  const handleSubmit = (e) => {
    e.preventDefault();
    if (title.trim()) {
      editingTask
        ? onUpdateTask(editingTask.id, { title, description })
        : onAddTask({ title, description });
      setTitle('');
      setDescription('');
      if (onCancel) onCancel();
    }
  };

  return (
    <form onSubmit={handleSubmit} className="task-form">
      <h3>{editingTask ? 'Edit Task' : 'Add New Task'}</h3>
      <input
        type="text"
        value={title}
        onChange={(e) => setTitle(e.target.value)}
        placeholder="Task Title"
        required
      />
      <textarea
        value={description}
        onChange={(e) => setDescription(e.target.value)}
        placeholder="Description (optional)"
      />
      <div className="form-buttons">
        <button type="submit">{editingTask ? 'Update' : 'Add'}</button>
        {onCancel && <button type="button" onClick={onCancel} className="cancel">Cancel</button>}
      </div>
    </form>
  );
};

const TaskItem = ({ task, onToggleComplete, onDeleteTask, onEditTask }) => {
  const [confirm, setConfirm] = useState(false);

  const formatDate = (dateStr) =>
    new Date(dateStr).toLocaleString(undefined, {
      year: 'numeric', month: 'short', day: 'numeric',
      hour: '2-digit', minute: '2-digit'
    });

  return (
    <div className={`task-item ${task.completed ? 'completed' : ''}`}>
      <div className="task-main">
        <button onClick={() => onToggleComplete(task.id)} className="check-btn">
          {task.completed && <Check size={14} />}
        </button>
        <div className="task-info">
          <h4>{task.title}</h4>
          {task.description && <p>{task.description}</p>}
          <small>Created: {formatDate(task.createdAt)}</small>
        </div>
        <div className="task-actions">
          <button onClick={() => onEditTask(task)}><Edit2 size={16} /></button>
          <button onClick={() => setConfirm(true)}><Trash2 size={16} /></button>
        </div>
      </div>
      {confirm && (
        <div className="confirm-box">
          <p>Are you sure to delete this task?</p>
          <button onClick={() => onDeleteTask(task.id)} className="danger">Delete</button>
          <button onClick={() => setConfirm(false)}>Cancel</button>
        </div>
      )}
    </div>
  );
};

const TaskFilter = ({ currentFilter, onFilterChange, taskCounts }) => {
  const filters = ['all', 'pending', 'completed'];

  return (
    <div className="task-filter">
      {filters.map((key) => (
        <button
          key={key}
          className={currentFilter === key ? 'active' : ''}
          onClick={() => onFilterChange(key)}
        >
          {key.charAt(0).toUpperCase() + key.slice(1)} ({taskCounts[key]})
        </button>
      ))}
    </div>
  );
};

const SearchBar = ({ searchTerm, onSearchChange }) => (
  <div className="search-bar">
    <Search className="search-icon" />
    <input
      type="text"
      value={searchTerm}
      onChange={(e) => onSearchChange(e.target.value)}
      placeholder="Search tasks..."
    />
  </div>
);

const App = () => {
  const [currentUser, setCurrentUser] = useState(getStoredUser());
  const [tasks, setTasks] = useState([]);
  const [showForm, setShowForm] = useState(false);
  const [editingTask, setEditingTask] = useState(null);
  const [filter, setFilter] = useState('all');
  const [searchTerm, setSearchTerm] = useState('');

  useEffect(() => {
    if (currentUser) setTasks(getStoredTasks());
  }, [currentUser]);

  useEffect(() => {
    if (currentUser) storeTasks(tasks);
  }, [tasks, currentUser]);

  const handleAddTask = ({ title, description }) => {
    const newTask = {
      id: Date.now(),
      title,
      description,
      completed: false,
      createdAt: new Date().toISOString()
    };
    setTasks([...tasks, newTask]);
    setShowForm(false);
  };

  const handleToggleComplete = (id) =>
    setTasks(tasks.map((t) => t.id === id ? { ...t, completed: !t.completed } : t));

  const handleDeleteTask = (id) =>
    setTasks(tasks.filter((t) => t.id !== id));

  const handleEditTask = (task) => {
    setEditingTask(task);
    setShowForm(true);
  };

  const handleUpdateTask = (id, updates) => {
    setTasks(tasks.map((t) => t.id === id ? { ...t, ...updates } : t));
    setShowForm(false);
    setEditingTask(null);
  };

  const filteredTasks = tasks.filter((task) => {
    const matchSearch = task.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
                        task.description.toLowerCase().includes(searchTerm.toLowerCase());
    if (filter === 'completed') return task.completed && matchSearch;
    if (filter === 'pending') return !task.completed && matchSearch;
    return matchSearch;
  });

  const taskCounts = {
    all: tasks.length,
    pending: tasks.filter((t) => !t.completed).length,
    completed: tasks.filter((t) => t.completed).length
  };

  const logout = () => {
    clearUser();
    setCurrentUser(null);
    setTasks([]);
  };

  if (!currentUser) return <Login onLogin={setCurrentUser} />;

  return (
    <div className="container">
      <header className="header">
        <div className="user-info">
          <User size={24} />
          <div>
            <h1>Task Tracker</h1>
            <p>Welcome, {currentUser}</p>
          </div>
        </div>
        <button onClick={logout} className="logout"><LogOut size={16} /> Logout</button>
      </header>

      <main>
        <div className="task-header">
          <h2>My Tasks</h2>
          <button onClick={() => setShowForm(true)}><Plus size={16} /> Add Task</button>
        </div>

        <SearchBar searchTerm={searchTerm} onSearchChange={setSearchTerm} />
        <TaskFilter currentFilter={filter} onFilterChange={setFilter} taskCounts={taskCounts} />
        {showForm && (
          <TaskForm
            onAddTask={handleAddTask}
            onCancel={() => {
              setShowForm(false);
              setEditingTask(null);
            }}
            editingTask={editingTask}
            onUpdateTask={handleUpdateTask}
          />
        )}

        {filteredTasks.length === 0 ? (
          <div className="no-tasks">
            <Filter size={40} />
            <p>No tasks found</p>
          </div>
        ) : (
          filteredTasks.map((task) => (
            <TaskItem
              key={task.id}
              task={task}
              onToggleComplete={handleToggleComplete}
              onDeleteTask={handleDeleteTask}
              onEditTask={handleEditTask}
            />
          ))
        )}
      </main>
    </div>
  );
};

export default App;
